from typing import *
class Solution:
    def lengthOfLIS(self, nums: List[int]) -> int:
        piles = []
        prev = {} # (pile index, card number) -> card number
        for num in nums:
            left, right = 0, len(piles)
            while left != right:
                mid = (left + right)//2
                if piles[mid][-1] >= num:
                    right = mid
                else:
                    left = mid + 1

            if left == len(piles):
                piles.append([])

            piles[left].append(num)
            if left != 0:
                prev[(left, num)] = piles[left-1][-1]

        print(piles)

        return len(piles)




